{

  state =
  {
    counter: 0
  }


  lol ()
  {
    this.setState({counter: this.state.counter + 1});
  }

  render ()
  {
    //console.log
    return(
      <div>
        <h1> nimel: {this.state.counter} </h1>

        <button onClick={() => this.lol(1)}/*{() => this.setState({counter: this.state.counter + 1})} */ >
          incrementa
        </button>

        <button onClick={() => this.setState({counter: this.state.counter - 1})}>
          decrementa
        </button>
      </div>
    );
  }




}

export default App;
